mapper.select:
select d.id, d.name, d.category_id, d.price, d.image, d.description, d.status, d.create_time, d.update_time, d.create_user, d.update_user, c.name category_name from dish d left outer join category c on c.id = d.category_id
d.name like concat('%', #{name}, '%')
and d.category_id = #{categoryId}
and d.status = #{status}
order by d.update_time desc

mapper.delete:
delete from dish
where id in

    #{id}

mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select * from dish


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}


order by create_time desc

select count(id) from dish


        and status = #{status}


        and category_id = #{categoryId}
